# coding:utf-8
class Solution:
    def checkInclusion(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """
        if len(s1) > len(s2):
        	return False
        dict1 = [0] * 26
        dict2 = [0] * 26
        for l in s1:
            dict1[ord(l)-ord('a')] += 1 
        
        
        
        left, right = 0,len(s1) - 1
        i = 0
        for i in range(len(s1)):
            dict2[ord(s2[i])-ord('a')] += 1
        
        
        

        if self.check2dict(dict1,dict2):
            return True
        #left = 1, right = len(s1) - 1 + 1
        while right < len(s2) - 1:
            
            left += 1 
            right += 1

            dict2[ord(s2[left-1])-ord('a')] -= 1
            dict2[ord(s2[right])-ord('a')] += 1
            

            
            if self.check2dict(dict1,dict2):
                return True
        
        return False
    
    def check2dict(self,dict1,dict2):
        for i in range(26):
            if dict1[i] != dict2[i]:
                return False
        
        return True

if __name__ == '__main__':
	solution = Solution()
	s1 = 'vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz'
	s2 = ""
	print(solution.checkInclusion(s1,s2))
